var header = "<table><thead><tr><th></th><th></th></tr></thead><tbody>";
var data = datasources["Connections"]["data"]["allConnections"]["edges"];
var dataRows = data.map(edge => `<tr><td>${edge.node.destIP}</td><td>${edge.node.sourcePort}</td></tr>`).join('');
var footer = "</tbody></table>";
return header + dataRows + footer;
